import React from 'react';
import { TouchableOpacity } from 'react-native';
import { View, Text, StyleSheet, Image, Dimensions, ScrollView,SafeAreaView} from 'react-native';

// image dimensions
const Imagewidth = Dimensions.get('screen').width / 2 - 60;
const mainWidth = Dimensions.get('screen').width /1.1;
const height = Dimensions.get('screen').height / 2.5;

const ProductFilter = (props) => {
    const {searchProducts} = props
    console.log("Search:", searchProducts)
    return (
       <ScrollView style={{height: height, marginLeft:10}}>
         <View style={styles.container}>
            {searchProducts.length > 0 ? (
                searchProducts.map((result) => (
                    <TouchableOpacity 
                        onPress={() => {
                            props.navigation.navigate('Product Details', {productList: result})
                        }} 
                        style={{flex: 1}} >
                        <View style={styles.displayContainer} >
                       <View style={styles.imageContainer}>
                         <Image source={{uri: result.img ? result.img : "https://icon-library.com/images/no-image-icon/no-image-icon-0.jpg"}} style={styles.image} />
                       </View>
                       <View style={styles.textContainer} key={result._id}>
                            <Text style={styles.resultText1}>{result.productName}</Text>
                            <Text style={styles.resultText2}>{result.productDescription.length > 0 ?  result.productDescription.substring(0, 50) + '...' : result.productDescription }</Text>
                            <View style={styles.addToCartContainer}>
                                <Text style={styles.addToCartPrice}>${result.price}</Text>
                                <Text style={styles.addToCartBtn}>{result.keepTrackProducts > 0 ? "ADD TO CART" : <Text style={{color: "red"}}>Not Available</Text>}</Text>
                            </View>
                       </View>
                     </View>
                    </TouchableOpacity> 
                ))
            ) : (
                <View>
                    <Text style={styles.text}>No Products Found</Text>
                </View>
            )}
        </View>
       </ScrollView>
    )
}

const styles = StyleSheet.create({
    resultContainer: {
        flexDirection: 'row',
    },

    container: {
       flex: 1,
        width: mainWidth,
        marginLeft: 10,
        marginTop: 20,
        marginBottom: 50,
    },

    displayContainer: {
        flex: 1,
        flexDirection: 'row',
        backgroundColor: '#fff',
        marginTop: 10,
        borderRadius: 10,
        
    },

    imageContainer: {
        margin: 10,
    },

    image: {
        width: 140,
        height: 300,
        marginLeft: -20,
        marginTop: 20,
        resizeMode: 'contain',
        height: Imagewidth  - 20 - 20,
    },

    textContainer: {
        flex: 1,
        marginTop: 30,
    },

    addToCartContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 20,
    },

    resultText1: {
        fontSize: 19,
        fontWeight: 'bold',
    },

    resultText2: {
        fontSize: 15,
    },

    addToCartPrice: {
        fontSize: 15,
        fontWeight: 'bold',
    },

    addToCartBtn: {
        fontSize: 13,
        fontWeight: 'bold',
        color: '#1662A2',
        marginRight: 10,
    }
})

export default ProductFilter;
